from typing import Final, Optional, Tuple

HashAlgorithm: Final = str = HashAlgorithmType

def hash_string(algorithm: HashAlgorithm, data: bytes, key: Optional[bytes]) -> bytes:
    # Import the necessary library
    import hashlib

    if algorithm not in HASH_ALGORITHMS:
        raise ValueError(f'Invalid hash algorithm: {algorithm}')

    hasher = hashlib.new(algorithm)
    if key:
        hasher.update(key.encode())
    hasher.update(data)
    return hasher.digest()

HASH_ALGORITHMS: Final = frozenset(['sha1', 'sha256', 'sha384', 'sha512'])
