from _lzma import open, FILTER_LZMA, DECOMPRESS, FORCE_INPUT

@total_ordering
class CompressedFile:
    def __init__(self, path: str):
        self.path = path

    def decompress(self) -> bytes:
        with open(self.path, mode='rb', format=FILTER_LZMA) as f:
            data = b''
            while True:
                chunk = f.read(8 * 1024)
                if not chunk:
                    break
                data += chunk
            return data
