from typing import Any,
        Callable, TypeVar,
        Union

Markup = _MarkupBase
Tag = Markup.tag
Atomic = Tag("atomic", attributes=True)
Content = Tag("content")

Text = Union[str, Atomic]
Attrs = Union[dict[str, Text], None]

def render(attrs: Attrs) -> Markup:
    return Content(*listify_attrs(attrs))

def listify_attrs(attrs: Attrs) -> tuple[Text, ...]:
    if attrs is None:
        return ()
    elif isinstance(attrs, dict):
        items = tuple(attrs.items())
        return (Tag("attr", name=k, value=v) for k, v in items)
    else:
        assert False,"Invalid attribute type"
