from fastapi import FastAPI, WebSocket
app = FastAPI()

@app.websocket('/ws')
def websocket_connect(websocket: WebSocket):
    print('Connected to a new WebSocket')

from fastapi import FastAPI, WebSocket, Receive, Send
@app.websocket('/ws')
def websocket_connect(websocket: WebSocket):
    await websocket.accept()
    while True:
        data = await websocket.receive_text()
        print(f'Received: {data}')
        await websocket.send_text('Echo: ' + data)
