from pylong import LongStr
from typing import TypeVar, Generic, List,
Callable, Tuple

MyList = TypeVar('MyList')
MyCallable = Callable[[LongStr], Tuple[str, int]]

class MyClass(Generic[MyList]):
    def __init__(self, my_list: MyList):
        self.my_list = my_list
    def process_long_strings(self, func: MyCallable) -> List[Tuple[str, int]]:
        result = []
        for item in self.my_list:
            result.append(func(item))
        return result
