code_examples.code:
from typing import Union, Callable

def generate_random_integer(low: int, high: int, random_fn: Callable[[], float]) -> int:
    return int(low + (high - low) * random_fn())

from typing import Optional

def random_or_default(random_fn: Callable[[], Union[float, str]], default: Optional[Union[float, str]] = None) -> Union[float, str]:
    return random_fn() if callable(random_fn) else default
