from typing import Union
from datetime import datetime, strptime

def parse_datetime(input_date: Union[str, None] = None) -> Union[datetime, None]:
   if input_date is not None:
       return strptime(input_date, '%Y-%m-%d %H:%M:%S')
   else:
       return None

from typing import List
def combine_dates(dates: Union[List[str], None] = None) -> Union[List[datetime], None]:
   if dates is not None:
       return [strptime(date, '%Y-%m-%d %H:%M:%S') for date in dates]
   else:
       return None
