from fastapi import FastAPI
app = FastAPI()
@app.get('/items/{item_id}')
def get_item(item_id: int):
    return {'item': f'Item {item_id}'}
if __name__ == '__main__':
    import uvicorn
    uvicorn.run('main:app', host='127.0.0.1', port=8000, log_level='info')
