code_examples.code:
from tkinter import Tk, Label
from typing import TypeVar, Generic,
Callable, Union

Window = TypeVar('Window', bound=Tk)
LabelWidget = TypeVar('LabelWidget', bound=Label)

def create_window(func: Callable[[Window], None]) -> Window:
    window = Tk()
    func(window)
    window.mainloop()

def create_label(window: Window, text: str) -> LabelWidget:
    label = Label(window, text=text)
    label.pack()
    return label

if __name__ == '__main__':
    create_window(lambda window: create_label(window, 'Hello, World!'))
