from typing import Any, Callable, List, Optional, Union

import _wmi

class WMIManager:
    def __init__(self) -> None:
        self.connect()

    def connect(self) -> None:
        self._connection = _wmi.WMI('winmgmt')

    def query(self, query: str) -> List[_wmi.WINClass]:
        result = self._connection.query(query)
        return list(result)

# Example usage:
manager = WMIManager()
instances = manager.query('SELECT * FROM Win32_Process')
