code_examples.code:
from datetime import datetime
from _datetime import timezone
from typing import Union

UTC: Union[timezone.utc, str] = typevar('UTC')
Local: Union[timezone.local, str] = typevar('Local')

def now_utc(tz: UTC = ...) -> datetime:
    if isinstance(tz, str):
        tz = timezone(tz)
    return datetime.now(tz)

from typing import TypeVar, Callable

T = TypeVar('T')
F = TypeVar('F', bound=Callable[[T], T])

def compose(*fs: F) -> F:
    def f(x: T) -> T:
        return fs[0](fs[1](...(fs[-1](x))))
