from abc import ABC, abstractmethod
from typing import TypeVar, Generic, Optional

T = TypeVar('T')

class AbstractContainer(ABC, Generic[T]):
  @abstractmethod
  def add_item(self, item: T) -> None:
      pass

  @property
  @abstractmethod
  def size(self) -> int:
      pass

class ConcreteContainer(AbstractContainer[int]):
  def __init__(self):
      self._items = []

  def add_item(self, item: int) -> None:
      self._items.append(item)

  @property
  def size(self) -> int:
      return len(self._items)
