import base64
from typing import BinaryIO, Union

def encode_base64(data: Union[bytes, BinaryIO]):
    if isinstance(data, bytes):
        return base64.b64encode(data).decode('utf-8')
    else:
        with data as fobj:
            return base64.b64encode(fobj.read()).decode('utf-8')

def decode_base64(data: str, output_type: Union[bytes, BinaryIO]):
    decoded = base64.b64decode(data)
    if isinstance(output_type, bytes):
        return decoded
    else:
        with open('output.bin', 'wb') as fobj:
            fobj.write(decoded)
        return fobj
