import typing

class Base64EncodedBinaryIO(io.BufferedReader):
    def __init__(self, data: Union[str, bytes]):
        if isinstance(data, str):
            data = base64.b64decode(data)
        super().__init__(io.BytesIO(data))

def read_base64_encoded_file(filename: str) -> Base64EncodedBinaryIO:
    with open(filename, 'r') as fobj:
        return Base64EncodedBinaryIO(fobj.read())
