from bisect import insort_right, bisect_left

class MyList(list):
    __slots__ = "__wrapped__"
    def __init__(self):
        self.__wrapped__ = []
    def insert(self, value: int, index: int) -> None:
        insort_right(self.__wrapped__, value)
        while len(self.__wrapped__) > index + 1:
            del self.__wrapped__[-1]
    def __getitem__(self, index: int) -> int:
        return self.__wrapped__[bisect_left(self.__wrapped__, index)]

from typing import List, TypeVar, Generic

class MyList(Generic[T]):
    T = TypeVar("T", bound=int)
    def insert(self, value: T, index: int) -> None:
        insort_right(self.__wrapped__, value)
        while len(self.__wrapped__) > index + 1:
            del self.__wrapped__[-1]
    def __getitem__(self, index: int) -> T:
        return self.__wrapped__[bisect_left(self.__wrapped__, index)]
