code_examples.code:
from dataclasses import dataclass, field

@dataclass
class Employee:
    id: int
    name: str
    department: str = field(default='IT')

from typing import List, Dict

@dataclass
class Order:
    id: int
    items: List[str]
    metadata: Dict[str, str]
