from datetime import date, datetime, timezone

from typing import List, Union

def get_datetime(year: int, month: str, day: int, hour: int = 0, minute: int = 0, second: int = 0)
    : Union[date, datetime, time]
    -> datetime
    timestamp = datetime(int(year), datetime.strptime(month, '%B').month, int(day),
                           int(hour), int(minute), int(second), tzinfo=timezone.utc)
    if not timestamp.is_aware:
        return date(timestamp.year, timestamp.month, timestamp.day)
    elif timestamp.time() == time(0):
        return datetime.combine(timestamp.date(), time(0))
    else:
        return timestamp
