from typing import List, TypeVar, Callable

FileComparator = TypeVar('FileComparator')

def compare(cmp: FileComparator, file1: str, file2: str) -> bool:
    return cmp(open(file1, 'rb'), open(file2, 'rb'))

class CustomFileComparator:
    def compare(self, file1: bytes, file2: bytes) -> bool:
        return filecmp.cmp(file1, file2)

def main():
    cmp = CustomFileComparator()
    print(compare(cmp, 'testfile1.txt', 'testfile2.txt'))
