code_examples.code:
from typing import Generic, TypeVar, List, Tuple

PathT = TypeVar('PathT')
class Path(Generic[PathT]):
    __args__ = (PathT,)
    def __init__(self, path: PathT):
        self.path: PathT = path

from genericpath import isdir, isfile

class PathUtils:
    @staticmethod
    def is_dir(path: Path) -> bool:
        return isdir(path.path)
    @staticmethod
    def is_file(path: Path) -> bool:
        return isfile(path.path)

from typing import List, Tuple

from mymodule.pathutils import Path, PathUtils

paths = [Path('/home/user'), Path('test.txt')]

is_dirs, is_files = zip(*(PathUtils.is_dir(path), PathUtils.is_file(path)) for path in paths)

print(f'Paths: {[str(path) for path in paths]}
is_dirs: {list(is_dirs)}
is_files: {list(is_files)}
