from fastapi import FastAPI
app = FastAPI()
@app.get('/')
def root():
 return {'message': 'Hello World!'}

from pydantic import BaseModel
class Item(BaseModel):
 name: str
 description: str
 class Items(BaseModel):
 items: List[Item]
@app.get('/items/')
def get_items():
 return Items(items=[{"name": "Foo", "description": "Bar"}])
