code_examples.code:
from typing import Dict, Any, Callable,
	TypeVar,
	gettext as _

TranslatedDict = TypeVar('TranslatedDict', bound=Dict[str, Any])

def translate(func: Callable[[str], str]) -> Callable[[TranslatedDict], str]:
    def wrapper(*args: tuple, **kwargs: Dict[str, Any]):
        if 'messages' not in kwargs:
            raise ValueError('missing `messages` parameter')

        message = func(*args, **kwargs)
        return _(
            message,
            domain='your_domain',
            use=gettext.LOCALE_LOADERS_LIST[1].load_domain_file,
        )
    return wrapper

@translate
def greet(name: str) -> str:
    'Returns translated greeting message'
    return f'Hello, {name}!'
