code_examples.code:
from fastapi import FastAPI
app = FastAPI()
@app.get('/items/{item_id}')
def read_item(item_id: int, q: str = None):
    return {'item_id': item_id, 'q': q}

from pydantic import BaseModel
class Item(BaseModel):
    name: str
    description: str
@app.post('/items/')
def create_item(item: Item):
    return item
