from typing import Generic, TypeVar, Iterable, TextIO

T = TypeVar('T')
class GzippedFile(Generic[T]):
    def __init__(self, fp: TextIO, mode: str = 'rb'):
        self.gz_file = gzip.GzipFile(mode=mode, fileobj=fp)
    def read(self) -> T:
        return pickle.loads(self.gz_file.read())
    def write(self, obj: T):
        self.gz_file.write(pickle.dumps(obj))
    def close(self):
        self.gz_file.close()
