from typing import cast, Final, Tuple

HashFunc: Final = Hash256

class Hash256:
    def __init__(self):
        self.hash = b''

    def update(self, data: bytes) -> None:
        self.hash = hashlib.sha256((self.hash + data)).digest()

    def hexdigest(self) -> str:
        return self.hash.hex()

from typing import cast

def hash_user_input(hashfunc: HashFunc, input: str) -> str:
    hashed = cast(HashFunc, hashfunc).update(input.encode())
    return hashed.hexdigest()
