from typing import Any, NoReturn, TypeVar, Optional, Union

IPv4Address = TypeVar('IPv4Address', bound='IPv4Address')
IPv6Address = TypeVar('IPv6Address', bound='IPv6Address')

def ip_address(ip: Union[IPv4Address, IPv6Address]) -> Any:
    if isinstance(ip, ipaddress.IPv4Address):
        return str(ip)
    elif isinstance(ip, ipaddress.IPv6Address):
        return str(ip)
    else:
        raise TypeError('Invalid IP address type')

ip_example = ipaddress.IPv4Address(192, 0, 2, 1)
print(ip_address(ip_example))
