from linecache import getline

def get_function_definition(filename: str, function_name: str) -> str:
    with open(filename) as f:
        for num, line in enumerate(f):
            if line.startswith(f'def {function_name}('):
                return getline(filename, num + 1)

# Example usage
function_definition = get_function_definition(__file__, 'my_function')
