from typing import List, Callable,
def greet(names: List[str], greeting: str, locale: Callable[[str], str]) -> None:
    for name in names:
        print(greeting.format(locale('LC_TIME', 'name', name)))

from locale import getdefaultlocale, setlocale,
# Set the preferred locale
supported = ['en_US', 'fr_FR']
for loc in supported:
    try:
        setlocale(lc_all, loc)
        break
    except:
        continue

# Get current locale
lc = getdefaultlocale()
