from typing import TypedDict, cast

class NetrcCredentials(TypedDict):
    host: str
    user: str
    passwd: str

def get_netrc_credentials(service: str) -> NetrcCredentials:
    try:
        import netrc
        credentials = cast(NetrcCredentials, netrc.netrc('~/.netrc').authenticators()[service].sections['default'])
    except Exception as e:
        raise ValueError(f'Error while reading netrc file: {e}')

# Usage
credentials = get_netrc_credentials('example_service')
