from typing import Protocol, TypeVar, List, Any,

class Container(Protocol):
    items: List[Any]

T = TypeVar('T')

def combine_lists(c1: Container[T], c2: Container[T]) -> Container[T]:
    result = [item for container in (c1, c2) for item in container.items] 
    return result
