code_examples.code:
```
from __future__ import annotations

import typing

class MyClass:
    __slots__ = ('data',)
    data: typing.List[int]

def dumps(obj: MyClass) -> bytes:
    import pickle
    return pickle.dumps(obj, protocol=pickle.HIGHEST_PROTOCOL)

def loads(data: bytes) -> MyClass:
    import pickle
    return pickle.loads(data)
