  "code_examples": [
    {
      "language": "python",
      "code": """
import typing
from pkgutil import iter_modules

def load_module(package: str) -> typing.Dict[str, typing.Type]:
    modules = {}
    for loader, name, ispkg in iter_modules(package):
        if ispkg:
            continue
        modules[name] = loader.find_module(name).load_type()
    return modules
      """
