def read_plist(file: str, plist_class: Type[NSObject]) -> plist_class:
    with open(file, 'rb') as f:
        return plistlib.frombytes(f.read(), 'big-endian')

    # Example usage:
    plist = read_plist('path/to/your/plist.plist', NSDictionary)
