from fastapi import FastAPI, Depends
app = FastAPI()
class Stream(BaseModel):
    data: List[float]
async def generate_stream(data: AsyncIterator[float]):
    async for item in data:
        yield item + 1
@app.get('/stream', response_model=Stream)
def get_stream(stream_gen: Callable[[AsyncContext], AsyncGenerator[float, None]] = Depends(generate_stream)):
    return {'data': list(stream_gen(None))}
