code_examples.code:
from typing import Optional, IO

def safe_open(file: str, mode: str = 'r', buffering: int = -1) -> Optional[IO]:
  try:
    return open(file, mode, buffering)
  except FileNotFoundError:
    return None

from os import listdir, remove
from typing import List[str], Optional[str]

def delete_files(directory: str, to_remove: List[str] = []) -> Optional[str]:
  if not directory.endswith('/'):
    directory += '/'
  for file in to_remove:
    filepath = directory + file
    if listdir(filepath):
      subdir = filepath + '/'
      delete_files(subdir, [file])
    else:
      try:
        remove(filepath)
      except Exception as e:
        print(f'Error deleting {filepath}: {e}')
