code_examples.code:
from typing import TypedDict, Union

class PathInfo(TypedDict):
    dirpath: str
    filename: str
    extension: str

def get_path_info(fullpath: Union[str, PosixPath]) -> PathInfo:
    import os
    parts = os.path.splitdrive(fullpath) if isinstance(fullpath, str) else fullpath.parts
    return {'dirpath': parts[0], 'filename': parts[1], 'extension': parts[2]} if parts[-1] else {
        'dirpath': '',
        'filename': parts[0],
        'extension': ''
    }

from typing import Any, Callable
from pathlib import PosixPath

def combine_dirs(path: Union[str, PosixPath], *dirs: str) -> Union[PosixPath, str]:
    paths = [PosixPath(d) for d in dirs]
    return PosixPath(path).joinpath(*paths)

def is_file(func: Callable[[Union[str, PosixPath]], Any]) -> Callable[[str], bool]:
    def wrapper(fullpath: Union[str, PosixPath]):
        return func(fullpath) and fullpath.is_file()
    return wrapper
