from typing import TypedDict, Union, List, Tuple

class Item(TypedDict):
    data: Union[str, int]

queue = mp.Queue()
items: List[Item] = []

def worker():
    while True:
        item = queue.get()
        items.append(item)
        queue.task_done()

if __name__ == '__main__':
    processes = 4
    for _ in range(processes):
        p = Process(target=worker)
        p.start()
