```

from fastapi import FastAPI, HTTPException
from hypothesize import given,
   strategies as st

app = FastAPI()

given(st.integers(min_value=1, max_value=99), st.integers(min_value=0, max_value=99))
def add(a: int, b: int):
    return a + b

@app.get('/add/{a}/{b}')
def get_addition(a: int, b: int):
    result = add(a, b)
    if result != a + b:
        raise HTTPException(status_code=500, detail='Test failed: calculation incorrect.')

@app.test_client().route('/{endpoint}')
def test_add(endpoint):
    response = client.get(endpoint.format(a=10, b=20))
    assert response.status_code == 200
    assert response.json() == {'result': 30}
```
