from typing import List, Tuple
from rlcompleter import Completer,
         DefaultCompleter

class CustomCompleter(DefaultCompleter):
  def get_completions(self, text, state):
    completions = super().get_completions(text, state)
    if text.endswith('('):
      # Custom completion for tuples
      for comp in [("Tuple[", f'[{completion}]') for completion in self.traverse_types(List[Tuple[str, int]])]:
        completions.insert(state, *comp)
    return completions
