from fastapi import FastAPI
app = FastAPI()
@app.get('/')
def main():
   return {'Hello': 'World'}

from fastapi.params import Body
from pydantic import BaseModel
class Item(BaseModel):
    name: str
    description: str
@app.post('/items/')
def create_item(item: Item):
   return item
