from typing import Final, Pattern, Union

RE_PATTERNS: Final = {
    'alpha': r'[a-zA-Z]+',
    'digit': r'\d+',
    'alphanum': r'[a-zA-Z0-9]+'
}

def match_pattern(text: str, pattern: Union[str, Pattern], group: int = 0) -> str:
    if isinstance(pattern, str):
        pattern = re.compile(RE_PATTERNS[pattern])
    return pattern.search(text, re.IGNORECASE).group(group)
