from typing import List, Dict, Optional, cast
import stat

def get_file_stats(paths: List[str], optional_fields: Optional[List[str]] = None) -> Dict[str, int]:
    if not optional_fields:
        optional_fields = []

    stats = {path: cast(Dict[stat.StandardFileAttributes, int], stat.stat(path)) for path in paths}

    for field in optional_fields:
        for path, stat_dict in stats.items():
            if field not in stat_dict:
                stats[path][field] = 0

    return stats
