from typing import List
from statistics import mean
def median(numbers: List[float]) -> float:
  numbers.sort()
  if len(numbers) % 2 == 0:
    return (numbers[len(numbers) // 2 - 1] + numbers[len(numbers) // 2]) / 2
  else:
    return numbers[len(numbers) // 2]
