code_examples.code:
from typing import Union

def safe_format(value: Union[str, int], format_string: str) -> str:
    return format_string.format(value)

safe_format('Hello, World!', 'Greetings: {0}')

from typing import List, Union

def safe_join(values: List[Union[str, int]], separator: str) -> str:
    return separator.join([str(v) for v in values])

safe_join([1, 2, 'Three', 4], ', ') == '1, 2, Three, 4'
