from typing import TypeVar, List, Callable, Tuple

FileNotFoundError = TypeVar("FileNotFoundError")
GetPathFunc = Callable[[str], str]

def get_python_executable(get_path: GetPathFunc) -> str:
    return get_path(__file__)

def get_site_packages_dir(get_path: GetPathFunc) -> Tuple[str, FileNotFoundError]:
    try:
        return (get_path(sysconfig.get_python_lib(platlib='py')) + '/site-packages', None)
    except FileNotFoundError:
        return ('', FileNotFoundError('Site packages directory not found.'))

from typing import List, Dict

def get_modules(module: str) -> Dict[str, List[str]]:
    module_info = sysconfig.get_config_info(module)
    modules = {} 
    for key, value in module_info.items():
        if 'sourcelist' in key:
            modules[key] = [item.strip() for item in value.split(':')]
    return modules
