from typing import TypedDict, List, IO

class TarInfoType(TypedDict):
    name: str
    size: int

def tar_extract(filename: str, dest_dir: str = '.', members: List[str] = None) -> None:
    with tarfile.open(filename, mode='r') as tar:
        if not members:
            tar.extractall(path=dest_dir)
        else:
            for member in members:
                tar.extract(member, path=dest_dir)

# Usage
filename = 'example.tar.gz'
dest_dir = './'
tar_extract(filename, dest_dir, ['example.txt', 'example_folder/'])```
