from typing import TextIO, List, Tuple

def format_text(file: TextIO, line_length: int = 80) -> List[str]:
    return textwrap.wrap(file.read(), width=line_length)

file: TextIO = open('example.txt', 'r')
formatted_lines: List[str] = format_text(file, line_length=72)
