from typing import List, Tuple

class Color(Protocol):
    pass

def set_color(turtle: Turtle, color: Color) -> None:
    turtle.color(color)

def main() -> None:
    # ... some setup code ...
    turtle = Turtle()
    set_color(turtle, (0, 1, 0))
