from fastapi import FastAPI
app = FastAPI()

def get_root():
    return {'Hello': 'World'}

app.get('/', get_root)

from fastapi.responses import WebSocketResponse
class WebSocketDemo:
    def __init__(self, ws: WebSocket):
        self.ws = ws
    async def connect(self):
        await self.ws.send_text('Connected!')
