from typing import TypedDict, List, Union

class NormalizedString(TypedDict):
    value: str

def normalize(text: Union[str, NormalizedString]) -> Union[NormalizedString, str]:
    if isinstance(text, NormalizedString):
        return text
    else:
        normalized = unicodedata.normalize('NFD', text)
        return {'value': normalized}
