from typing import Union, List
from uu import unionOf, is_union

class Animal:
    def __init__(self, name: str):
        self.name = name

class Dog(Animal):
    pass
class Cat(Animal):
    pass

Pets = unionOf(Dog, Cat)

def accept_pet(pet: Union[Dog, Cat]):
    if is_union(pet):
        pet.name += '_adopted'
    else:
        pet.name += '_visited'

pet = Pets('Fido', Dog)
accept_pet(pet)

